
var adicionar = document.querySelector(".adc");

adicionar.addEventListener('click',estacionar);

function estacionar(event) {
    event.preventDefault();
    var carro = document.getElementById("buscarmodelo").value;
var placa = document.getElementById("buscarplaca").value;

var tempo = new Date()

carro = {
    modelo : carro,
    placa : placa ,
    horas : tempo.getHours(),
    minutos : tempo.getMinutes()
     }

console.log(carro);

if(localStorage.getItem('garagem') === null ) {
var carros = [];
carros.push(carro);
localStorage.setItem('garagem',JSON.stringify(carros));
}else {
    var carros = JSON.parse(localStorage.getItem('garagem1'));
    carros.push(carro);
    localStorage.setItem('garagem',JSON.stringify(carros));

}

function showgaragem() {
    var carros = JSON.parse(localStorage.getItem('garagem1'));
    var garagemresultado = document.getElementById("result");
    garagemresultado.innerHTML="caralho"
}

     }